import React, { Component } from "react";
import {
  NavItem,
  Navbar,
  Icon,
  Dropdown,
  Divider,
  Button,
} from "react-materialize";
import { Redirect } from "react-router-dom";
import "materialize-css";
import globals from "./globals";

class Nawigacja extends Component {
  constructor() {
    super();

    this.state = {
      lang: localStorage.getItem("lang"),
    };
  }

  handleClickEn = () => {
    // localStorage.setItem("lang", "en");
    // window.location.href = "homeen";
  };
  handleClickPl = () => {

    localStorage.setItem("lang", "pl");
    window.location.href = "/homepl";
  };
  render() {
    if (this.state.lang == "en")
      return ( 
        <Navbar
          className="navbarmadejpak"
          alignLinks="right"
          brand={
            <img
              src="https://raw.githubusercontent.com/galeriapodsloncem/3dstrona/master/babylonjs-app/public/assets/zdjeciakonfigurator/logo.png"
              alt=""
              height="100%"
            />
          }
          //<p1 className="brand-logo white-text ">En PL Madej-Pak</p1>}
          leftLogo
          menuIcon={
            <div className="black-text  ">
              <h5>menu</h5>
              {/* <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b2/Hamburger_icon.svg/1024px-Hamburger_icon.svg.png" width="90%" alt=""/> */}
            </div>
          }
          options={{
            draggable: true,
            edge: "left",
            inDuration: 250,
            onCloseEnd: null,
            onCloseStart: null,
            onOpenEnd: null,
            onOpenStart: null,
            outDuration: 200,
            preventScrolling: true,
          }}
        >
          <div className="hide-on-med-and-up">
            <img
              src="https://raw.githubusercontent.com/galeriapodsloncem/3dstrona/master/babylonjs-app/public/assets/zdjeciakonfigurator/logo.png"
              alt=""
              width="100%"
            />
          </div>
          <NavItem className=" przycisk_nawigacja " href="home">
            <div className="white-text ">Home</div>
          </NavItem>
          <NavItem className=" przycisk_nawigacja " href="galeria">
            <div className="white-text">Gallery</div>
          </NavItem>{" "}
          <NavItem className=" przycisk_nawigacja " href="system">
            <div className="white-text ">Houses</div>
          </NavItem>{" "}
          <NavItem className=" przycisk_nawigacja " href="dodatki">
            <div className="white-text ">Addons</div>
          </NavItem>
          <NavItem className=" przycisk_nawigacja " href="zamow">
            <div className="white-text">Order</div>
          </NavItem>
          <NavItem className=" przycisk_nawigacja " href="faq">
            <div className="white-text">FAQ</div>
          </NavItem>
      {/*     <Dropdown
            id="Dropdown_991"
            options={{
              alignment: "left",
              autoTrigger: true,
              closeOnClick: true,
              constrainWidth: true,
              container: null,
              coverTrigger: true,
              hover: false,
              inDuration: 150,
              onCloseEnd: null,
              onCloseStart: null,
              onOpenEnd: null,
              onOpenStart: null,
              outDuration: 250,
            }}
            trigger={
              <a className=" przycisk_nawigacja ">
                {" "}
                <div className="white-text">Nasza Firma</div>{" "}
              </a>
            }
          >
            <a className="black-text" href="about">
              O Nas
            </a>

            <a className="black-text" href="kontrahenci">
              Kontrahenci
            </a>

            <a className="black-text" href="certyfikaty">
              Certyfikaty
            </a>

            <a className="black-text" href="kariera">
              Kariera
            </a>
          </Dropdown>*/}
          <NavItem className=" przycisk_nawigacja " href="contact"> 
            <div className="white-text">Contact</div>
          </NavItem>
          <Button
            onClick={(e) => this.handleClickPl(e)}
            className="przycisk_nawigacja_pl z-depth-2"
          ></Button>
          <Button
            onClick={(e) => this.handleClickEn(e)}
            className="przycisk_nawigacja_en z-depth-2"
          ></Button>
        </Navbar>
     );
    else
      return (
        <Navbar
          className="navbarmadejpak"
          alignLinks="right"
          brand={
            <img
              src="https://raw.githubusercontent.com/galeriapodsloncem/3dstrona/master/babylonjs-app/public/assets/zdjeciakonfigurator/logo.png"
              alt=""
              height="100%"
            />
          }
          //<p1 className="brand-logo white-text ">En PL Madej-Pak</p1>}
          leftLogo
          menuIcon={
            <div className="black-text  ">
              <h5>menu</h5>
              {/* <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b2/Hamburger_icon.svg/1024px-Hamburger_icon.svg.png" width="90%" alt=""/> */}
            </div>
          }
          options={{
            draggable: true,
            edge: "left",
            inDuration: 250,
            onCloseEnd: null,
            onCloseStart: null,
            onOpenEnd: null,
            onOpenStart: null,
            outDuration: 200,
            preventScrolling: true,
          }}
        >
          <div className="hide-on-med-and-up">
            <img
              src="https://raw.githubusercontent.com/galeriapodsloncem/3dstrona/master/babylonjs-app/public/assets/zdjeciakonfigurator/logo.png"
              alt=""
              width="100%"
            />
          </div>
          <NavItem className=" przycisk_nawigacja " href="home">
            <div className="white-text ">Home</div>
          </NavItem>  <NavItem className=" przycisk_nawigacja " href="system">
            <div className="white-text ">Domy</div>
          </NavItem>{" "}


          <NavItem className=" przycisk_nawigacja " href="dodatki">
            <div className="white-text ">Dodatki</div>
          </NavItem> <NavItem className=" przycisk_nawigacja " href="galeria">
            <div className="white-text">Galeria</div>
          </NavItem>{" "}
          <NavItem className=" przycisk_nawigacja " href="zamow">
            <div className="white-text">Zam√≥w</div>
          </NavItem>

      {/*     <Dropdown
            id="Dropdown_991"
            options={{
              alignment: "left",
              autoTrigger: true,
              closeOnClick: true,
              constrainWidth: true,
              container: null,
              coverTrigger: true,
              hover: false,
              inDuration: 150,
              onCloseEnd: null,
              onCloseStart: null,
              onOpenEnd: null,
              onOpenStart: null,
              outDuration: 250,
            }}
            trigger={
              <a className=" przycisk_nawigacja ">
                {" "}
                <div className="white-text">Nasza Firma</div>{" "}
              </a>
            }
          >
            <a className="black-text" href="about">
              O Nas
            </a>

            <a className="black-text" href="kontrahenci">
              Kontrahenci
            </a>

            <a className="black-text" href="certyfikaty">
              Certyfikaty
            </a>

            <a className="black-text" href="kariera">
              Kariera
            </a>
          </Dropdown>*/}
          <NavItem className=" przycisk_nawigacja " href="contact"> 
            <div className="white-text">Kontakt</div>
          </NavItem>
          <NavItem className=" przycisk_nawigacja " href="faq">
            <div className="white-text">FAQ</div>
          </NavItem>
          <Button
            onClick={(e) => this.handleClickPl(e)}
            className="przycisk_nawigacja_pl z-depth-2"
          ></Button>
          <Button
            onClick={(e) => this.handleClickEn(e)}
            className="przycisk_nawigacja_en z-depth-2"
          ></Button>
        </Navbar>
      );
  }
}
export default Nawigacja;


